import React from 'react'
import {Box,makeStyles, TextareaAutosize, Typography} from '@material-ui/core'
import {Delete, Edit} from '@material-ui/icons'
import AccessTimeIcon from '@material-ui/icons/AccessTime';

const styleClass =makeStyles({
    container:{
        display:"flex",
        flexDirection:"column",
    },
    commentbox:{
        display:"flex",
        flexDirection:"column"
    },
    header:{
        display:"flex",
        flexDirection:"row",
        marginTop:"25px",
        marginBottom:"5px"
        
    },
    timeicon:{
        marginLeft:"5px",
        opacity:"0.5"
    },
    date:{
        marginLeft:"5px",
        opacity:"0.6"
    },
    comment:{
        marginTop:"5px",
        marginBottom:"5px"
    },
    icon:{
        border:"1px solid #000",
        borderRadius:"5px",
        padding:"5px",
        margin:"5px"
    }
})

function Comment() {

    const styles=styleClass();

    return (
        <Box className={styles.container}>
           <Box className={styles.commentbox}> 
                <Box className={styles.header}>
                    <Typography className={styles.author}> Author Name</Typography>
                    <AccessTimeIcon className ={styles.timeicon}></AccessTimeIcon>
                    <Typography className={styles.date}>Date</Typography>
                </Box>
                <TextareaAutosize className={styles.comment}>Comment </TextareaAutosize>
            </Box>    
            <Box className={styles.buttons}>
                <Edit className={styles.icon} color="primary" />
                <Delete className={styles.icon} color="error" />
            </Box>    

        </Box>    
    )
}

export default Comment
