import React from 'react'
import {Box,colors,FormControl,InputBase,makeStyles, Typography} from '@material-ui/core'
import { borders } from '@material-ui/system';

const styleClass = makeStyles((theme)=>(
    {
        container:{
            display:"flex",
            alignItems:"center",
            justifyContent:"center",
            background:"linear-gradient(90deg, rgba(15,12,41,1) 0%, rgba(48,43,99,1) 36%, rgba(36,36,62,1) 100%);",
            height: window.innerHeight,
            flex:1,
            [theme.breakpoints.down("md")]:{
                display:"block"
            }
        },
        loginbox:{
            width:"35%",
            height:"70%",
            padding:"20px",
            display:"flex",
            background:"#FFFFFF",
            flexDirection:"row",
            justifyContent:"center",
            [theme.breakpoints.down("md")]:{
               width:"100%",
               border:"none",
               height:"100%"
            }
        },
        heading:{
            fontWeight:"900",
            fontSize:"28px",
            color:"#302b63"
        }
    }
))
const Login= ()=> {
    const styles=styleClass()
    return (
        <>
        <Box className={styles.container} >
            <Box border={2} borderColor="primary.main"  borderRadius={16} className={styles.loginbox}>
                <Typography className={styles.heading}>WELCOME TO BLOGSPOT</Typography>
                <Box className={styles.userdata}>
                    <FormControl>
                        UserName
                        <InputBase label></InputBase>
                        <InputLabel htmlFor="outlined-adornment-password">Password</InputLabel>
          <OutlinedInput
            id="outlined-adornment-password"
            type={values.showPassword ? 'text' : 'password'}
            value={values.password}
            onChange={handleChange('password')}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {values.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            }
            labelWidth={70}
          />
                    </FormControl>    

                </Box>
                <Box className={styles.sociallogin}></Box>

            </Box>    
        </Box>
        </> 
    )
}

export default Login
