import React from 'react'
import { useState,useEffect } from 'react';
import {Box, FormControl, makeStyles,Button, InputBase, TextareaAutosize} from '@material-ui/core'
import ImageSearchIcon from '@material-ui/icons/ImageSearch';
import { getOnePostData ,updatePostData } from '../service/api';

const styleclass=makeStyles(theme => ({
    container:{
        marginTop:"64px",
        padding:"0px 100px",
        [theme.breakpoints.down("md")]:{
            padding:"0px"
        }
        
    },
    image:{
        height:"50vh",
        width:"100%",
        objectFit:"cover",
        
    },
    icon:{
        marginTop:"7px",
        cursor:"pointer"
    },
    form:{
        display:"flex",
        width:"100%",
        flexDirection:"row",
        marginTop:"15px"
    },
    textfield:{
        flex:"1",
        margin:"0px 10px",
        fontSize:"25px"
    },
    textarea:{
        width:"100%",
        marginTop:"50px",
        border:"none",
        '&:focus-Visible':{
            outline:"none"
        },
        fontSize:"18px"
    }
})
)
/*Match is a default prop which can access various data like URL using match.params*/ 
function UpdatePost({match}) {
    const style=styleclass()

    const [post,setPost] =useState({})

    useEffect( () => {
        const fetchdata = async () =>{
            const data= await getOnePostData(match.params.id);
            setPost(()=> data)
        }
        fetchdata();
    }  
    ,[])


    return (
        <Box className={style.container}>
            <img className={style.image} src={post.image || "https://images.unsplash.com/photo-1543128639-4cb7e6eeef1b?ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8bGFwdG9wJTIwc2V0dXB8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80'"} alt="banner image" />
            <FormControl className={style.form}>
                <ImageSearchIcon className={style.icon}/>
                <InputBase className={style.textfield} placeholder="title of blog">{post.title} </InputBase> 
                <Button variant="contained" color="primary">Update</Button>
            </FormControl>
            <InputBase placeholder="Category">{post.category}</InputBase>
            <TextareaAutosize placeholder="Put Your Thoughts Here" minRows='5' className={style.textarea}>{post.description}
            </TextareaAutosize>
        </Box>    
    )
}

export default UpdatePost
