import React from 'react'
import {Box,colors,FormControl,InputBase,makeStyles, Typography} from '@material-ui/core'
import { borders } from '@material-ui/system';

const styleClass = makeStyles((theme)=>(
    {
        container:{
            display:"flex",
            alignItems:"center",
            justifyContent:"center",
            background:"linear-gradient(90deg, rgba(15,12,41,1) 0%, rgba(48,43,99,1) 36%, rgba(36,36,62,1) 100%);",
            height: window.innerHeight,
            flex:1,
            [theme.breakpoints.down("md")]:{
                display:"block"
            }
        },
        loginbox:{
            width:"35%",
            height:"70%",
            padding:"20px",
            display:"flex",
            background:"#FFFFFF",
            flexDirection:"column",
            alignItems:"center",
            [theme.breakpoints.down("md")]:{
               width:"100%",
               border:"none",
               height:"100%"
            }
        },
        heading:{
            fontWeight:"900",
            fontSize:"28px",
            color:"#302b63"
        },
        userdata:{
            width:"70%",
            padding:"20px"
        },
        sociallogin:{
            width:"70%",
            padding:"20px"
        }
    }
))
const Login= ()=> {
    const styles=styleClass()
    return (
        <>
        <Box className={styles.container} >
            <Box border={2} borderColor="primary.main"  borderRadius={16} className={styles.loginbox}>
                <Typography className={styles.heading}>WELCOME TO BLOGSPOT</Typography>
                <Box className={styles.userdata}>
                    <FormControl>
                        UserName
                        <InputBase ></InputBase>
                        PassWord
                       
                    </FormControl>    

                </Box>
                <Box className={styles.sociallogin}>
                <FormControl>
                        UserName
                        <InputBase ></InputBase>
                        PassWord
                       
                    </FormControl> 
                </Box>

            </Box>    
        </Box>
        </> 
    )
}

export default Login
