import React from 'react'
import { useState } from 'react';
import {Box, FormControl, makeStyles,Button, InputBase, TextareaAutosize} from '@material-ui/core'
import ImageSearchIcon from '@material-ui/icons/ImageSearch';

const styleclass=makeStyles(theme => ({
    container:{
        marginTop:"64px",
        padding:"0px 100px",
        [theme.breakpoints.down("md")]:{
            padding:"0px"
        }
        
    },
    image:{
        height:"50vh",
        width:"100%",
        objectFit:"cover",
        
    },
    icon:{
        marginTop:"7px",
        cursor:"pointer"
    },
    form:{
        display:"flex",
        width:"100%",
        flexDirection:"row",
        marginTop:"15px",
        [theme.breakpoints.down("xs")]:{
            display:"block"
        }
    },
    textfield:{
        flex:"1",
        margin:"0px 10px",
        fontSize:"25px",
        
    },
    button:{
        marginRight:"10px",
        [theme.breakpoints.down("xs")]:{
            display:"block"
        }
    },
    textarea:{
        width:"100%",
        marginTop:"50px",
        border:"none",
        '&:focus-Visible':{
            outline:"none"
        },
        fontSize:"18px"
    }
})
)

function CreatePost() {
    const style=styleclass()
    const initialvalues={
        title:'',
        catergory:'',
        username:'',
        date:new Date(),
        description:'',
        image:''
    }

    const {post,setPost} =useState(initialvalues)

    return (
        <Box className={style.container}>
            <img className={style.image} src="https://images.unsplash.com/photo-1543128639-4cb7e6eeef1b?ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8bGFwdG9wJTIwc2V0dXB8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80'" alt="banner image" />
            <FormControl className={style.form}>
                <ImageSearchIcon className={style.icon}/>
                <InputBase className={style.textfield} name="title" placeholder="title of blog" /> 
                <Button variant="contained" color="primary" className={style.button}>Create</Button>
            </FormControl>
            <TextareaAutosize placeholder="Put Your Thoughts Here" name="description" minRows='5' className={style.textarea}>

            </TextareaAutosize>
        </Box>    
    )
}

export default CreatePost
