import React from 'react'
import {Box, makeStyles, TextareaAutosize, Typography} from '@material-ui/core'
import { Delete,Edit } from '@material-ui/icons';
const styleclass=makeStyles(theme=>({
    container:{
        padding:"0px 100px"
    },
    image:{
        height:"50vh",
        width:"100%",
        margin:"0px 20px 0px 20px",
        objectFit:"cover",
        
    },
    icon:{
        border:"1px solid #000",
        borderRadius:"5px",
        padding:"2px",
        margin:"10px"
    }   
}))

function DisplayPost() {
    const style=styleclass();
    return (
        <Box className={style.container}>
            <img className={style.image} src='https://images.unsplash.com/photo-1543128639-4cb7e6eeef1b?ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8bGFwdG9wJTIwc2V0dXB8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80'/>
            <Box>
                <Edit className={style.icon} />
                <Delete className={style.icon} />
                <Typography>Title of the blog</Typography>
                <Typography>Author</Typography>
                <Typography>Date</Typography>
                <TextareaAutosize >
                    Data of the Blog
                </TextareaAutosize>
            </Box>
        
        </Box>
    )
}

export default DisplayPost
