import React from 'react'
import {useState,useEffect} from 'react-dom'
import {Box, makeStyles, StylesProvider, TextareaAutosize, Typography} from '@material-ui/core'
import { Delete,Edit} from '@material-ui/icons';
import  History  from './../History';
import { getOnePostData } from '../service/api';

const styleclass=makeStyles(theme=>({
    container:{
        margin: '50px 100px',
        [theme.breakpoints.down('md')]: {
            margin: 0
        },
    },
    image:{
        height:"50vh",
        width:"100%",
        objectFit:"cover",
        
    },
    icon:{
        border:"1px solid #000",
        borderRadius:"5px",
        padding:"5px",
        margin:"5px"
    },
    icons:{
        float:"right"
    },
    title:{
        textAlign:"center",
        margin:"50px 0px 10px 10px ",
        fontSize:"30px",
        fontWeight:"600"
    },
    subhead:{
        display:"flex",
        flexDirection:"row",
        marginBottom:"20px",
        [theme.breakpoints.down("sm")]:{
            display:"block"
        }
    },
    date:{
        marginLeft:"auto"
    },  
}))

function DisplayPost() {
    const style=styleclass();
    const [post,setPost]=useState([])

    useEffect((_id)=>{
        const fetchdata = async(_id) =>{
            let data=getOnePostData(_id)
            setPost(()=>data)
            console.log(data);
        }
    },[])


    return (
        <Box className={style.container}>
            <img className={style.image} src='https://images.unsplash.com/photo-1543128639-4cb7e6eeef1b?ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8bGFwdG9wJTIwc2V0dXB8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80'/>
            <Box className={style.icons}>
                <Edit onClick={()=> History.push("/update")} className={style.icon} color="primary" />
                <Delete className={style.icon} color="error"/>
            </Box>
            <Typography className={style.title}>{post.title}</Typography>
            <Box className={style.subhead}>    
                <Typography className={style.author}>Author : {post.username}<span>Utkarsh Bahukhandi</span></Typography>
                <Typography className={style.date}>Date : {post.date}</Typography>
            </Box>
            <Typography>{post.category}</Typography>
            <Typography >
            {post.description}
            </Typography>
            
        
        </Box>
    )
}

export default DisplayPost
